<?php

namespace Database\Seeders;

use App\Models\MaritimeScenario;
use Illuminate\Database\Seeder;

/**
 * Populate PASSENGER scenarios with production-quality content.
 *
 * Idempotent: updates existing rows by scenario_code.
 * Run: php82 artisan db:seed --class=PassengerScenarioContentSeeder --force
 */
class PassengerScenarioContentSeeder extends Seeder
{
    public function run(): void
    {
        $scenarios = array_merge(
            $this->getScenariosSlot1to4(),
            $this->getScenariosSlot5to8(),
        );

        foreach ($scenarios as $code => $data) {
            MaritimeScenario::where('scenario_code', $code)->update($data);
            $this->command->info("Updated: {$code}");
        }

        $activated = MaritimeScenario::where('command_class', 'PASSENGER')
            ->where('version', 'v2')
            ->update(['is_active' => true]);

        $this->command->info("PASSENGER scenario content seeded and activated ({$activated} scenarios).");
    }

